function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),t=document.querySelector("body"),s=document.querySelector(".nav__btns");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),s.classList.remove("active")):(c.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"),s.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),s.classList.remove("active"))}))}burgerMenu();const btn=document.querySelector(".play__btn"),clip=document.querySelector(".clip"),close=document.querySelector(".close"),video=document.querySelector("video");btn.onclick=function(){btn.classList.add("active"),clip.classList.add("active"),video.play(),video.currentTime=0},close.onclick=function(){btn.classList.remove("active"),clip.classList.remove("active"),video.pause()},clip.onclick=function(){btn.classList.remove("active"),clip.classList.remove("active"),video.pause()};